import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const express = require('express');
const cors = require('cors');
const cursosRouter = require('./src/databases/cassandra/cursos');
const matricularRouter = require('./src/databases/cassandra/matricular');

const app = express();
const PORT = 3001;

// Middlewares
app.use(cors()); // Permite solicitudes desde el frontend
app.use(express.json()); // Para leer JSON en POST

// Rutas
app.use('/src/databases/cassandra/cursos', cursosRouter);
app.use('/src/databases/cassandra/matricular', matricularRouter);

// Ruta base de prueba
app.get('/', (req, res) => {
  res.send('Servidor Express corriendo correctamente');
});

// Iniciar servidor
app.listen(PORT, () => {
  console.log(`Servidor Express corriendo en http://localhost:${PORT}`);
});

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
