import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../shared/Form.css'; 

export default function Matricula() {
  const [cursos, setCursos] = useState([]);
  const userId = 'efcfe460-a2b6-11ee-b9d1-0242ac120002'; // ejemplo fijo, luego dinámico

  useEffect(() => {
    axios.get('http://localhost:3001/cursos')
      .then(res => {
        console.log('Cursos recibidos:', res.data);
        setCursos(res.data);
      })
      .catch(err => console.error('Error al obtener cursos:', err));
  }, []);

  const handleMatricular = (courseId) => {
    axios.post('http://localhost:3001/matricular', { userId, courseId })
      .then(() => alert('¡Matriculado correctamente!'))
      .catch(err => alert('Error al matricular'));
  };

  return (
    <div className="container">
      <div className="header">
        <div className="text">Cursos Disponibles</div>
        <div className="underline"></div>
      </div>

      <div className="inputs">
        {cursos.length === 0 ? (
          <p style={{ textAlign: 'center', color: 'white' }}>No hay cursos disponibles en este momento.</p>
        ) : (
          cursos.map((curso) => (
            <div key={curso.course_id} className="input" style={{ justifyContent: 'space-between', padding: '0 20px' }}>
              <div>
                <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#fff' }}>{curso.title}</div>
                <div style={{ fontSize: '14px', color: '#eee' }}>
                  Inicio: {new Date(curso.start_date).toLocaleDateString()}
                </div>
              </div>
              <button
                className="submit"
                onClick={() => handleMatricular(curso.course_id)}
                style={{ width: 'auto', padding: '10px 20px', height: '40px', fontSize: '16px' }}
              >
                Matricular
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
