import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../shared/Form.css'; 

export default function Matricula() {
  const [cursos, setCursos] = useState([]);
  const userId = 'efcfe460-a2b6-11ee-b9d1-0242ac120002'; // ejemplo fijo, luego dinámico

  useEffect(() => {
    axios.get('http://localhost:3001/cursos')
      .then(res => {
        console.log('Cursos recibidos:', res.data);
        setCursos(res.data)})
      .catch(err => console.error('Error al obtener cursos:', err));
  }, []);

  const handleMatricular = (courseId) => {
    axios.post('http://localhost:3001/matricular', { userId, courseId })
      .then(() => alert('¡Matriculado correctamente!'))
      .catch(err => alert('Error al matricular'));
  };

  return (
    <div className="contenedor-matricula">
      <h2>Cursos</h2>
      <div className="tabla-cursos">
        {cursos.length === 0 ? (
          <p>No hay cursos disponibles en este momento.</p>
        ) : (
          cursos.map(curso => (
            <div key={curso.course_id} className="fila-curso">
              <span>{curso.title}</span>
              <span>{new Date(curso.start_date).toLocaleDateString()}</span>
              <button onClick={() => handleMatricular(curso.course_id)}>Matricular</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
